SELECT student.no, student.name, student.gender, student.level, student.stay_type, student.student_status, student.teacher_id, student.community_teacher_id, department.name department, profession.name profession, direction.name direction, class.name classes, area.name area, floor.name floor, room.name room, teacher.name teacher_name, teacherCommunity.name community_teacher_name FROM t_student student
LEFT JOIN (SELECT id, name FROM t_department) department ON department.id = student.department_id
LEFT JOIN (SELECT id, name FROM t_direction) direction ON direction.id = student.direction_id
LEFT JOIN (SELECT id, name FROM t_profession) profession ON profession.id = student.profession_id
LEFT JOIN (SELECT id, name FROM t_classes) class ON class.id = student.classes_id
LEFT JOIN (SELECT id, name FROM t_area) area ON area.id = student.area_id
LEFT JOIN (SELECT id, name FROM t_floor) floor ON floor.id = student.floor_id
LEFT JOIN (SELECT id, name FROM t_room) room ON room.id = student.room_id LEFT JOIN (SELECT t_teacher.no, name, gender FROM t_teacher) teacher ON teacher.no = student.teacher_id LEFT JOIN (SELECT t_teacher.no, name, gender FROM t_teacher) teacherCommunity ON teacherCommunity.no = student.community_teacher_id
WHERE
student.name LIKE #{searchDto.name} and student.no LIKE #{searchDto.studentNo} and student.gender LIKE #{searchDto.sex}
and student.classes_id LIKE #{searchDto.classesId}
and student.level = #{searchDto.level}
and student.department_id = #{searchDto.departmentId}
and student.direction_id = #{searchDto.directionId}
and student.profession_id = #{searchDto.professionId}
and student.teacher_id = #{searchDto.teacherId}
and student.community_teacher_id = #{searchDto.communityTeacherId}
and student.stay_type = #{searchDto.TypeOfAccommodation}
and student.student_status = #{searchDto.studentStatusSearch}
and student.area_id = #{searchDto.areaId}
and student.floor_id = #{searchDto.floorId}
and room.name LIKE #{searchDto.roomId}
ORDER BY department.id DESC,level DESC ,profession.id ASC,direction.id DESC,class.id ASC limit #{start},#{pageSize};

